    public static void getAllAffilStringsUnrestricted(final JsonNode node, Set<String> affils) {


        Iterator<Map.Entry<String, JsonNode>> fieldsIterator = node.fields();

        while (fieldsIterator.hasNext()) {
            Map.Entry<String, JsonNode> field = fieldsIterator.next();

            final String key = field.getKey();
            //System.out.println("Key: " + key);
            final JsonNode value = field.getValue();

            if (value.isContainerNode()) {


                Iterator<JsonNode> iter = value.elements();

                while (iter.hasNext()) {

                    getAllAffilStringsUnrestricted(iter.next(), affils); //recursion

                }


            } else {


                if ("name".equals(key)) {


                    System.out.println(value.textValue());

                    // System.out.println("key/Value: " + key + " " + value + " " + value.getNodeType());
                    affils.add(value.asText());
                }

            }
        }


    }

    public static void buildRawAffilStringForAuthor(final JsonNode node, List<String> parts) {


        Iterator<Map.Entry<String, JsonNode>> fieldsIterator = node.fields();

        while (fieldsIterator.hasNext()) {
            Map.Entry<String, JsonNode> field = fieldsIterator.next();

            final String key = field.getKey();
            //System.out.println("Key: " + key);
            final JsonNode value = field.getValue();

            if (value.isContainerNode()) {


                Iterator<JsonNode> iter = value.elements();

                while (iter.hasNext()) {

                    buildRawAffilStringForAuthor(iter.next(), parts); //recursion

                }


            } else {


                if ("name".equals(key)) {

                    parts.add(value.textValue());

                  // if(stringBuilder.length() == 0) { stringBuilder.append(value.textValue()); } else { stringBuilder.append(", ").append(value.textValue()); }

                 //   System.out.println(value.textValue());

                    // System.out.println("key/Value: " + key + " " + value + " " + value.getNodeType());

                }

            }
        }

    }

    public static void collectAllHasAffiliationNodes(final JsonNode node, List<JsonNode> affils) {


        Iterator<Map.Entry<String, JsonNode>> fieldsIterator = node.fields();

        while (fieldsIterator.hasNext()) {
            Map.Entry<String, JsonNode> field = fieldsIterator.next();

            final String key = field.getKey();
            //  System.out.println("Key: " + key);
            if("hasAffiliation".equals(key)) affils.add( field.getValue() );

            final JsonNode value = field.getValue();

            if (value.isContainerNode()) {


                Iterator<JsonNode> iter = value.elements();

                while (iter.hasNext()) {

                    collectAllHasAffiliationNodes( iter.next(), affils ); //recursion

                }


            } else {

                //not implemented here

            }
        }


    }

    public static Set<String> processAllHasAffiliationNodes(List<JsonNode> hasAffiliationNodeList) {

        HashSet<String> authorizedAffiliationUris = new HashSet<>();

        for(JsonNode jsonArray : hasAffiliationNodeList) {

            for(JsonNode jsonObject : jsonArray) {


                //perhaps just id for country etc

                JsonNode type = jsonObject.get("@type");
                JsonNode auth = jsonObject.get("identifiedBy");

                if ( type != null && auth != null && "Organization".equals(type.textValue())) {

                    //debug
                    if(auth.size() != 1) {System.out.println("Auth array != 0, : " + jsonObject); System.exit(0); }

                    JsonNode authType = auth.get(0).get("@type");
                    if(authType != null && "URI".equals( authType.textValue() )) authorizedAffiliationUris.add( auth.get(0).get("value").textValue() );

                }


            } //for each object

        } //for hasAffiliation array

        return authorizedAffiliationUris;

    }


