    public static List<String> getRawAffiliationsPerAuthor(JsonNode contributorNode) {

        if(contributorNode == null) return Collections.emptyList();

        List<String> rawAffiliations = new ArrayList<>();


        for (JsonNode contributionObject : contributorNode) { //array of contributors

            JsonNode agent = contributionObject.get("agent");
            //process only persons and their affiliations
            if (!"Person".equals(agent.get("@type").textValue())) continue;
            //todo process agent object w r t name and local id and OIRCID

            //no affiliation information at all
            if( !contributionObject.has("hasAffiliation") ) continue; //System.out.println("No affiliation information for a person: " + id ); //TODO missing KI here!

            JsonNode affiliations = contributionObject.get("hasAffiliation"); //potentially several per author

          //  System.out.println(affiliations.getNodeType() + " " + affiliations.size()); //array of different length

            //build up the string recursively here

            for(JsonNode node : affiliations) {

               List<String> parts = new ArrayList<>();
               buildRawAffilStringForAuthor(node,parts);

                if(parts.size() != 0) {

                    StringBuilder stringBuilder = new StringBuilder();

                    for(int i=parts.size()-1; i>=0; i--) {

                      if(i==parts.size()-1)  { stringBuilder.append( parts.get(i) ); } else {   stringBuilder.append(", ").append(parts.get(i));    }
                    }

                    rawAffiliations.add(stringBuilder.toString());

                }



            }



        } //for each contributor



        return rawAffiliations;

    }


